DELIMITER //
CREATE DEFINER=`root`@`localhost` PROCEDURE `ambientesDisp`(IN fec DATE, h TIME, typese INTEGER, sed_id INTEGER)
BEGIN

SELECT ta.amb_id,ta.amb_den,  ts.ser_edu_id, ts.ser_edu_hin
FROM  tbl_ambiente ta left JOIN tbl_servicio_educativo ts
ON ta.amb_id=ts.amb_id 
and
ts.ser_edu_fec=fec
and 
(


	time( 
		time(ts.ser_edu_hin)
			+
		time(
			(SELECT tss.tip_serv_durac FROM tbl_tipo_servicio tss 
			INNER JOIN tbl_servicio_educativo se
			ON tss.tip_serv_id=se.tip_serv_id where
			se.ser_edu_id=ts.ser_edu_id)*10000
			)
		)>=time(h)

and
	time(
		time(ts.ser_edu_hin) -
		time(
			(SELECT tss.tip_serv_durac 
			FROM tbl_tipo_servicio tss where tss.tip_serv_id=typese )*10000
			)
	)<time(h)


)
inner join tbl_sede sede on sede.sed_id=ta.sed_id and ta.sed_id=sed_id;


END //

-- FINISH PROCEDURE AMBIENTES DISPONIBLES

